export function post(endpoint, data) {
    console.log(444);
    console.log(endpoint);
    const fetch = process.browser ? window.fetch : require('node-fetch').default;
    return fetch(endpoint, {
        method: 'POST',
        credentials: 'include',
        body: JSON.stringify(data),
        headers: {
            'Content-Type': 'application/json'
        }
    }).then(r => r.json());
}